import { requireNativeModule } from 'expo';
import { useFonts } from 'expo-font';
import { useState } from 'react';
import { ImageBackground, SafeAreaView, StyleSheet, StatusBar} from 'react-native';
import { colors } from './src/Global/colors';
import Home from './src/Screens/Home';
import ItemListCategories from './src/Screens/ItemListCategories';
import * as Animatable from 'react-native-animatable'


const App = () => {
  const [categorySelected, setCategorySelected] = useState('')
  const backGroundImage = {uri: 'https://legacy.reactjs.org/logo-og.png'};
  const [fontLoaded] = useFonts({
    JosefinSB: require('./assets/fonts/Josefin_Sans/static/JosefinSans-SemiBold.ttf'),
    JosefinM: require('./assets/fonts/Josefin_Sans/static/JosefinSans-Medium.ttf'),
    Josefin: require('./assets/fonts/Josefin_Sans/JosefinSans-VariableFont_wght.ttf'),
    Lobster: require('./assets/fonts/Lobster/Lobster-Regular.ttf')
  })

  if (!fontLoaded) return null

  return (
    <SafeAreaView style={styles.container}>
        {categorySelected ?
          <ItemListCategories category={categorySelected} setCategory={setCategorySelected} />
          :
          <Home setCategorySelected={setCategorySelected} />
        }
    </SafeAreaView>
  )
}
export default App

const styles = StyleSheet.create({
  container: {

    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    width: "100%",
  },
  text: {
    fontSize: 20,
  },
  input: {
    backgroundColor: colors.color3,
    width: "80%",
    borderWidth: 2,
    borderRadius: 5,
    paddingHorizontal: 10,
    paddingVertical: 5,
    margin: 10,
  },
  image: {
    flex: 1,
    justifyContent: 'center',
  },
});
